INSERT INTO `wpod_posts` VALUES("586", "1", "2019-10-18 08:14:22", "2019-10-18 08:14:22", "<h2>Quy Trình Giao Hàng</h2>		\n		<p>Chúng tôi sẽ chia sẻ với bạn nhiều hơn về qúa trình giao hàng cũng như cách nhận hàng như thế nào.</p><h2 style=\"margin-bottom: 13px; padding: 0px; font-family: Montserrat, sans-serif; line-height: 1.2; color: #0c1325; font-size: 28px;\">I. QUY CHUẨN ĐÓNG GÓI</h2> <ul style=\"margin-bottom: 10px; padding-left: 40px; color: #0c1325; font-family: Montserrat, sans-serif;\"><li style=\"text-align: justify;\">Đóng gói bằng thùng, hộp bằng bìa cứng bên ngoài (trừ các sản phẩm không bể vỡ và biến dạng như quần áo, vải v…v). </li><li style=\"text-align: justify;\">Sản phẩm được bọc kín, gia cố bằng mút, xốp, giấy báo bên trong hộp đóng gói để không bể vỡ trong suốt quá trình vận chuyển. </li><li style=\"text-align: justify;\">Đối với hàng hóa có chứa chất lỏng hoặc thực phẩm cần được bọc bằng bao bì chống thấm. </li><li style=\"text-align: justify;\">Nếu sản phẩm là hàng dễ vỡ, khuyến khích Khách hàng dán nhãn “hàng dễ vỡ” bên ngoài bao bì đóng gói.</li></ul>", "DỊCH VỤ", "", "inherit", "closed", "closed", "", "568-revision-v1", "", "", "2019-10-18 08:14:22", "2019-10-18 08:14:22", "", "568", "http://cafey123.epizy.com/2019/10/18/568-revision-v1/", "0", "revision", "", "0");
